api.controller = function($rootScope, $scope, spModal, spUtil) {
    var c = this;
    c.data.blockUpdate = true;
    c.onLoad = function() {
        console.log('OnLoad');
        c.server.get({
            action: 'getCIAttrs'
        }).then(function(r) {
            c.data.ciAttributes = r.data.ciAttributes;
            c.data.printerData = r.data.deviceData;
        });
        c.server.get({
            action: 'getUserSettings'
        }).then(function(r) {
            c.data.userSettings = r.data.userSettings;
        });
    }
    $rootScope.$on('selectPrinter', function(event, data) {
        console.log('Listener caught PrinterID: ' + $rootScope.printerSysID);
        c.server.get({
            action: 'getData',
            printerSysID: $rootScope.printerSysID,
            recordType: $rootScope.recordType
        }).then(function(r) {
            c.data.printerData = r.data.printerData;
            c.data.blockUpdate = r.data.blockUpdate;
            if (c.data.userSettings.lockedLocation) {
                c.data.printerData.ec.location = c.data.userSettings.location;
                c.data.printerData.ec.location_dv = c.data.userSettings.location_dv;
            }
            if (c.data.userSettings.lockedEntityDEP) {
                c.data.printerData.ec.epic_entity = c.data.userSettings.entity;
                c.data.printerData.ec.epic_entity_dv = c.data.userSettings.entity_dv;
                c.data.printerData.ec.epic_dep = c.data.userSettings.dep;
                c.data.printerData.ec.epic_dep_dv = c.data.userSettings.dep_dv;
            }
            api.populateSNRecords($scope, c.data.printerData);
            for (i = 0; i < c.data.ciAttributes.length; i++) {
                if (c.data.printerData.ec.printer_attributes_list.toString().includes(c.data.ciAttributes[i].sys_id)) {
                    c.data.ciAttributes[i].isChecked = true;
                } else {
                    c.data.ciAttributes[i].isChecked = false;
                }
            }
        });
    });
    c.updatePrinter = function() {
        c.data.printerData.ec.epic_printer_model = $scope.epicModel.value;
        c.data.printerData.ec.epic_printer_model_dv = $scope.epicModel.displayValue;
        c.data.printerData.ec.model_dv = $scope.epicModel.displayValue;
        c.data.printerData.ec.model_id = $scope.model.value;
        c.data.printerData.ec.model_id_dv = $scope.model.displayValue;
        c.data.printerData.ec.epic_entity = $scope.epicEntity.value;
        c.data.printerData.ec.epic_entity_dv = $scope.epicEntity.displayValue;
        c.data.printerData.ec.epic_dep = $scope.epicDepartment.value;
        c.data.printerData.ec.epic_dep_dv = $scope.epicDepartment.displayValue;
        c.data.printerData.ec.location = $scope.location.value;
        c.data.printerData.ec.location_dv = $scope.location.displayValue;
        var missingValues = [];
        if (c.data.printerData.ec.ctrlNum == '')
missingValues.push(' - Control Number/Name');
        if (c.data.printerData.ec.location == '')
            missingValues.push(' - Location');
        if (c.data.printerData.ec.room_cube == '')
missingValues.push(' - Room/Cube');
        if (c.data.printerData.ec.epic_entity == '')
            missingValues.push(' - Epic Entity');
        if (c.data.printerData.ec.epic_printer_model == '' && c.data.printerData.ec.missing_model == '')
missingValues.push(' - Epic Printer Model/Missing Model');
        if (c.data.printerData.ec.printer_type == '')
            missingValues.push(' - Printer Type');
        if (c.data.printerData.ec.tray_type == '')
            missingValues.push(' - Tray Type');
        if (c.data.printerData.ec.tray == '')
            missingValues.push(' - Tray');
        if (missingValues.length > 0) {
            var missingMessage = "Required fields are missing. Please check your entries and try again.\n";
            missingMessage += missingValues.join('\n');
            alert(missingMessage);
            return;
        }
        var ciAttrCompile = [];
        for (i = 0; i < c.data.ciAttributes.length; i++) {
            if (c.data.ciAttributes[i].isChecked == true) {
                ciAttrCompile.push(c.data.ciAttributes[i].sys_id.toString());
            }
        }
        c.data.printerData.ec.printer_attributes = ciAttrCompile.join(',');
        c.server.get({
            action: 'updatePrinter',
            inputValue: c.data.printerData
        }).then(function(r) {
            c.data.updateStatus = r.data.updateStatus;
            c.data.printerData = r.data.printerData;
            if (c.data.updateStatus != null) {
                spUtil.addInfoMessage(c.data.printerData.ec.ctrlNum + ' updated!');
                var id = {};
                id.printerSysID = c.data.printerData.ec.sys_id;
                id.searchValue = c.data.printerData.ec.ctrlNum;
                api.populateSNRecords($scope, c.data.printerData);
                $rootScope.$emit('updateSearch', id);
            } else
                spUtil.addErrorMessage('Update failed!');
        });
    }
    c.isBlocked = function() {
        if (c.data.blockUpdate == true)
return 'blocked';
        else
            return '';
    }
    c.hasData = function(value) {
        var className = '';
if (c.data.blockUpdate == false) {
            if (value == "")
                className = 'required-text';
        }
        return className;
    }
    c.hasDataSNRecord = function(value) {
        var className = '';
if (c.data.blockUpdate == false) {
            switch (value) {
                case "model":
                    if ($scope.model.value == '')
                        className = 'required-text';
                    break;
                case "location":
                    if ($scope.location.value == '')
                        className = 'required-text';
                    break;
                case "epicEntity":
                    if ($scope.epicEntity.value == '')
                        className = 'required-text';
                    break;
                case "epicModel":
                    if ($scope.epicModel.value == '')
                        className = 'required-text';
                    break;
            }
        }
        return className;
    }
    c.updateLocks = function() {
        if (c.data.userSettings.lockedLocation) {
            if ($scope.location.value != '') {
                c.data.userSettings.location = $scope.location.value;
                c.data.userSettings.location_dv = $scope.location.displayValue;
            } else
                c.data.userSettings.lockedLocation = false;
        } else {
            c.data.userSettings.location = '';
            c.data.userSettings.location_dv = '';
        }
        if (c.data.userSettings.lockedEntityDEP) {
            if ($scope.epicEntity.value != '' && $scope.epicDepartment.value != '') {
                c.data.userSettings.entity = $scope.epicEntity.value;
                c.data.userSettings.entity_dv = $scope.epicEntity.displayValue;
                c.data.userSettings.dep = $scope.epicDepartment.value;
                c.data.userSettings.dep_dv = $scope.epicDepartment.displayValue;
            } else
                c.data.userSettings.lockedEntityDEP = false;
        } else {
            c.data.userSettings.entity = '';
            c.data.userSettings.entity_dv = '';
            c.data.userSettings.dep = '';
            c.data.userSettings.dep_dv = '';
        }
        console.log('userSettings:\n' + JSON.stringify(c.data.userSettings, null, 2));
        c.server.get({
            action: 'updateUserSettings',
            userSettings: c.data.userSettings
        }).then(function(r) {
            c.data.epicModelData = r.data.epicModelData;
        });
    }
    c.onEpicModelChange = function() {
        c.server.get({
            action: 'getEpicPrinterDefaults',
            epicModelSysID: $scope.epicModel.value
        }).then(function(r) {
            c.data.epicModelData = r.data.epicModelData;
            if (c.data.epicModelData) {
                c.data.printerData.ec.printer_type = c.data.epicModelData.printer_type;
                if (c.data.printerData.ec.tray_type == 'Default Tray') {
                    c.data.printerData.ec.tray = c.data.epicModelData.default_tray;
                }
            }
        });
    }
    c.hasData_epicModel = function() {
        var className = '';
if (c.data.blockUpdate == false) {
            if ($scope.epicModel.value == '' && c.data.printerData.ec.missing_model == '')
                className = 'required-text';
        }
        return className;
    }
    c.hasData_hostname = function() {
        var className = '';
if (c.data.blockUpdate == false) {
            if (c.data.printerData.ec.hostname == '' && c.data.printerData.ec.ip_address == '')
                className = 'required-text';
        }
        return className;
    }
}
api.populateSNRecords = function($scope, printerData) {
    console.log('populateSNRecords');
    $scope.model = {
        displayValue: printerData.ec.model_id_dv,
        value: printerData.ec.model_id,
        name: 'model'
    };
    $scope.epicModel = {
        displayValue: printerData.ec.epic_printer_model_dv,
        value: printerData.ec.epic_printer_model,
        name: 'epicModel'
    };
    $scope.cmdbPrinter = {
        displayValue: printerData.ec.cmdb_printer_dv,
        value: printerData.ec.cmdb_printer,
        name: 'cmdbPrinter'
    };
    $scope.currentLocation = {
        displayValue: printerData.ec.current_location_dv,
        value: printerData.ec.current_location,
        name: 'currentLocation'
    };
    $scope.location = {
        displayValue: printerData.ec.location_dv,
        value: printerData.ec.location,
        name: 'location'
    };
    $scope.currentEpicEntity = {
        displayValue: printerData.ec.current_epic_entity_dv,
        value: printerData.ec.current_epic_entity,
        name: 'currentEpicEntity'
    };
    $scope.epicEntity = {
        displayValue: printerData.ec.epic_entity_dv,
        value: printerData.ec.epic_entity,
        name: 'epicEntity'
    };
    $scope.currentEpicDepartment = {
        displayValue: printerData.ec.current_epic_dep_dv,
        value: printerData.ec.current_epic_dep,
        name: 'currentEpicDepartment'
    };
    $scope.epicDepartment = {
        displayValue: printerData.ec.epic_dep_dv,
        value: printerData.ec.epic_dep,
        name: 'epicDepartment'
    };
}
